import React, { Component, useState } from 'react'
import Modal from 'react-modal'
import Base from "../core/Base";
import Menu from "../core/Menu"
import Addexpense from './Addexpense';
import {Button, ButtonToolbar} from 'react-bootstrap'
import FriendTrans from './FriendTrans'



class UserDashBoard extends Component {

  constructor(props){
    super(props);

    this.state = {
      friends: [],
      owes: [],
      isLoaded: false,
      addModalShow: false,
      name : ''
    };
    this.onFormSubmit = this.onFormSubmit.bind(this);
  }

  onFormSubmit(name) {
    this.setState({name});
  }




  componentDidMount(){
    fetch('http://localhost:3000/friends')
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        friends: json,
      })
    });

    fetch('http://localhost:3000/friends?owe=true')
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        owes: json,
      })
    });
  }


  render(){

    var { isLoaded, friends } = this.state;

    var { isLoaded, frndswhooweu } = this.state;

    let addModalClose = () => this.setState({addModalShow:false});

  return (
    <>
      <Menu />
      <div>
          <ul className="dash">
          <li><ButtonToolbar>
               <Button
                    className="btn-exp" 
                    variant='primary' 
                    onClick={()=> this.setState({addModalShow: true})}>Add Expense</Button>
                    <Addexpense show={this.state.addModalShow} 
                                onHide={addModalClose} 
                                onSubmit={this.onFormSubmit} 
                    />
               </ButtonToolbar>
          </li>
          <li><button className="btn-stl">Settle Up</button></li>
          </ul>   

              <div className="toto">
                <div>Total Balance</div>
                <div>You Owe</div>
                <div>You are Owed</div>
              </div>

              <div className="toto1">
                <div id="tom"><div>Friends Who Owe You</div>
                <div>
                {owes.map(friend => (
                  <div><ButtonToolbar>
                          <Button className="frndbutton" key={friend.id} 
                >{friend.name} </Button>
                          <FriendTrans show={this.state.addModalShow} onHide={addModalClose} onSubmit={this.onFormSubmit} />
                        </ButtonToolbar>
                  </div>
                ))};
                </div>
                </div>
                <div>Friends whom You owe</div>
              </div>

              <h2>Friends List</h2>
              <div>
                {friends.map(friend => (
                  <div><ButtonToolbar>
                          <Button className="frndbutton" key={friend.id} 
                                  onClick={()=> this.setState({addModalShow: true})}
                >{friend.name} </Button>
                          <FriendTrans show={this.state.addModalShow} onHide={addModalClose} onSubmit={this.onFormSubmit} />
                        </ButtonToolbar>
                  </div>
                ))};
              </div>


        </div>
</>

  );
}
}


export default UserDashBoard;
