import React, { Component } from 'react'
import {Modal, Button} from 'react-bootstrap'
import axios from 'axios'

class Addexpense extends Component{
    constructor(props){
        super(props);

        this.state ={
          name: '',
          amount: '',
          description: ''
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        
    }

    handleSubmit(e){
      e.preventDefault();
      // const data = new FormData(e.target);

      // this.setState({
      //   res: stringifyFormData(data),
      // });
      // fetch('http://localhost:3000/friends', {
      //   method:'POST',
      //   body: data
      // });
      // console.log(data)
      axios.post('http://localhost:3000/friends', this.state)
        .then(response => {
          console.log(response)
        })
        .catch(error => {
          console.log(error)
        }) 
    }

    changeHandler = e => {
      this.setState({[e.target.name] : e.target.value })
    }

    render(){

      const { name, amount, description } = this.state
        return(
            <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Add Friend Transaction
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <form onSubmit={this.handleSubmit}>
                <div className="input-form">
                     <input type="text" name="name" placeholder="name" onChange={this.changeHandler}/>
                 </div>
                 <div className="input-form">
                     <input type="number" name="amount" placeholder="$ 0.00" onChange={this.changeHandler}/>
                 </div>
                 <div className="input-form">
                     <input type="text" name="description" placeholder="description" onChange={this.changeHandler} />
                 </div>
                 <div className="input-form">
                     <p>Paid by you and Split equally </p>
                     <p>$0.00 /person</p>
                 </div>
                 <div>
                 <button className="btn">Add an image/notes</button>
                 </div>
                 <div>
                 <button type="submit">save</button>
                 </div>
               
             </form>
            
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={this.props.onHide}>Close</Button>
            </Modal.Footer>
          </Modal>
          );
    }
   
}



function stringifyFormData(fd) {
  const data = {};
	for (let key of fd.keys()) {
  	data[key] = fd.get(key);
  }
  return JSON.stringify(data, null, 2);
}

export default Addexpense;


